#include <string>

std::string solution(std::string input) {
    int count = 0;
    // Inefficient: doesn't handle uppercase
    for (int i = 0; i < input.length(); i++) {
        if (input[i] == 'a' || input[i] == 'e' || 
            input[i] == 'i' || input[i] == 'o' || 
            input[i] == 'u') {
            count++;
        }
    }
    return std::to_string(count);
}

solution_code:
#include <string>
#include <cctype>

std::string solution(std::string input) {
    int count = 0;
    for (char c : input) {
        char lower = std::tolower(c);
        if (lower == 'a' || lower == 'e' || 
            lower == 'i' || lower == 'o' || 
            lower == 'u') {
            count++;
        }
    }
    return std::to_string(count);
}
