solution_code:
#include <string>
#include <algorithm>

std::string solution(std::string input) {
    std::string result = input;
    int n = result.length();
    for (int i = 0; i < n / 2; i++) {
        char temp = result[i];
        result[i] = result[n - 1 - i];
        result[n - 1 - i] = temp;
    }
    return result;
}

