solution_code:
function debounce(func, wait) {
  let timeoutId = null;

  return function(...args) {
    const context = this;

    clearTimeout(timeoutId);

    timeoutId = setTimeout(() => {
      func.apply(context, args);
    }, wait);
  };
}

module.exports = { debounce };
